nta.declaration:
//type definitions:

typedef struct {
    int[-1,15] x;
    int[-1,15] y;
} Cell;

typedef struct {
    int health;
} MainTowerConfiguration;

typedef struct {
    int speed;
    int health;
    int damage;
    int spawningTime;
    bool kind;
} EnemyConfiguration;

typedef struct {
    int range;
    int speed;
    int damage;
} TurretConfiguration;

typedef struct {
    Cell pos;
    int lifeTime;
    bool kind;
    bool available;
} EnemyRecord;

typedef struct {
    int id;
    int damage;
} TargetRecord;

//configuration definitions:

const MainTowerConfiguration MT := {10};

const EnemyConfiguration SQUARE := {3, 20, 4, 3, true};
const EnemyConfiguration CIRCLE := {1, 10, 2, 2, false};

const TurretConfiguration BASIC := {2,2,2};
const TurretConfiguration CANNON := {1,7,5};
const TurretConfiguration SNIPER := {4,20,8};

//map definitions

const Cell MAIN_POS = {15,4};

const Cell TURRET0_POS = {5,5};
const Cell TURRET1_POS = {8,2};
const Cell TURRET2_POS = {8,6};
const Cell TURRET3_POS = {14,2};
const Cell TURRET4_POS = {14,6};
const Cell TURRET5_POS = {2,3};
const Cell TURRET6_POS = {11,5};

const Cell INVALID_POS = {-1,-1};
const Cell START_POS = {0,0};

const int MAX_PATH_LENGTH = 25;

//wave parameters

const int MAX_ENEMIES := 3; //how many enemies will eventually get into the match
int not_ended_enemies := MAX_ENEMIES; //how many enemies are currently alive or about to spawn
int available_enemies := 0; //how many enemies are currently available (already spawned and not dead)

bool mt_alive = true;

//channels and shared parameters

    //enemies --> MT



bool isValid (Cell c)
    {
        if (c == INVALID_POS)
            {
                return (true);
            }
        if (c.y == 7)
            {
                return (c.x>=7 && c.x<=15);
            } 
        if (c.y == 6 || c.y == 5)
            {
                return (c.x == 7 || c.x == 10 || c.x == 15);
            }
        if (c.y == 4)
            {
                return (c.x != 8 && c.x != 9);
            }
        if (c.y == 3 || c.y == 2)
            {
                return (c.x == 0 || c.x == 7 || c.x == 15);
            }
        if (c.y == 1)
            {
                return (c.x == 0 || (c.x >=7 && c.x<=15));
            }
        if (c.y == 0)
            {
                return (c.x == 0);
            }

        return (false);
    }
bool matchEnded()
    {
        return (not_ended_enemies == 0 || !mt_alive);
    }

template.declaration:
clock x;
Cell pos := INVALID_POS;
int lifeTime := 0;
int chosenPath := 0;

void initialize(int i)
    {
        pos = START_POS;
        chosenPath = i;

        shoot_table[id].lifeTime = 0;
        shoot_table[id].kind = kind;
        shoot_table[id].available = true;
        shoot_table[id].pos = pos;
        shoot_table[id].lifeTime = lifeTime;

        available_enemies++;

        x = 0;
    }
void shoot()
    {
        damage_to_mt = damage;
    }
void dismiss ()
    {
        shoot_table[id].available = false;
        not_ended_enemies--;
        available_enemies--;
    }
void takeDamage ()
    {
        if (target_record.id == id)
            {
                health = health - target_record.damage;
                target_record.damage = 0;
            }
    }

void updateRecord ()
    {
        shoot_table[id].pos = pos;
        lifeTime = lifeTime + spawningTime;
        shoot_table[id].lifeTime = lifeTime;        
    }
void next()
    {
        if (pos.x == 0)
            {
                if (pos.y <=3)
                    pos.y = pos.y+1;
                else
                    if (pos.y == 4)
                        pos.x = 1;

                return;
            }
        if (pos.x >=1 && pos.x <= 6)
            {
                pos.x = pos.x +1;
                return;
            }
        if (pos.x == 7)
            {
                if (pos.y == 4)
                    {
                        if (chosenPath == 0 || chosenPath == 1)
                            pos.y = pos.y + 1;
                        else
                            pos.y = pos.y - 1;
                    }
                else
                    if (pos.y == 5 || pos.y == 6)
                        pos.y = pos.y + 1;
                    else
                        if (pos.y == 3 || pos.y == 2)
                            pos.y = pos.y - 1;
                        else
                            if (pos.y == 7 || pos.y == 1)
                                pos.x = pos.x + 1;
                return;
            }
        if (pos.x >= 8 && pos.x <= 9)
            {
                if (pos.y == 7 || pos.y == 1)
                    pos.x = pos.x + 1;
                return;
            }
        if (pos.x == 10)
            {
                if (pos.y == 7)
                    {
                        if (chosenPath == 0)
                            pos.y = pos.y - 1;
                        else
                            pos.x = pos.x + 1;
                    }
                else
                    if (pos.y == 6 || pos.y == 5)
                        pos.y = pos.y - 1;
                    else
                        if (pos.y == 1 || pos.y == 4)
                            pos.x = pos.x + 1;
                return;
            }
        if (pos.x >= 11 && pos.x <= 14)
            {
                if (pos.y == 7 || pos.y == 1 || pos.y == 4)
                    pos.x = pos.x + 1;
                return;
            }
        if (pos.x == 15)
            {
                if (pos.y < 4 && pos.y >= 1)
                    pos.y = pos.y + 1;
                else
                    if (pos.y > 4 && pos.y <= 7)
                        pos.y = pos.y - 1;
                return;
            }
    }
bool movable ()
    {
        return (true);
    }

clock y = 0;

int max (int v1, int v2)
    {
        if (v1 >= v2)
            return (v1);
        else
            return (v2);
    }
int dist(const Cell c1, const Cell c2)
    {
        return (max(abs(c1.x - c2.x), abs(c1.y - c2.y)));
    }
void shoot (int id)
    {
        target_record.id = id;
        target_record.damage = damage;
    }
void target()
    {
        int i, app_i, cont=0, target_id, target_dist, target_lifeTime, d;

        if (available_enemies > 0)
            {
                i = 0;
                while (i < MAX_ENEMIES && !(shoot_table[i].available && dist(shoot_table[i].pos, pos)<=range))
                    {
                        i++;
                    }
                if (i < MAX_ENEMIES)
                    {
                        target_id = i;
                        target_dist = dist(shoot_table[i].pos, pos);
                        target_lifeTime = shoot_table[i].lifeTime;
                        cont++;
                        for (i = app_i+1 ; cont < available_enemies; i++)
                            {
                                if (shoot_table[i].available)
                                    {
                                        cont++;
                                        d = dist(shoot_table[i].pos, pos);
                                        if (d <target_dist)
                                            {
                                                target_id = i;
                                                target_dist = d;
                                                target_lifeTime = shoot_table[i].lifeTime;
                                            }
                                        else
                                            {        
                                                if (d == target_dist)
                                                    {
                                                        if (target_lifeTime < shoot_table[i].lifeTime)
                                                            {
                                                                target_id = i;
                                                                target_lifeTime = shoot_table[i].lifeTime;
                                                            }
                                                        else
                                                            {
                                                                if (target_lifeTime == shoot_table[i].lifeTime)
                                                                    {
                                                                        if (shoot_table[i].kind)
                                                                            {
                                                                                 target_id = i;
                                                                            }
                                                                    }
                                                            }
                                                    }
                                            }
                                       }
                            }

                        shoot (target_id);
                    }
                else
                    {
                        return;
                    }
            }
    }
bool shootable()
    {
        int i, cont;

        cont=0;
        for (i = 0 ; i<MAX_ENEMIES && cont<available_enemies; i++)
            {
                if (shoot_table[i].available)
                    {
                        cont++;
                        if (dist(shoot_table[i].pos, pos)<=range)
                           {
                               return (true);
                           }
                    }
            }

        return (false);
    }

void takeDamage()
    {
        life = life - damage_to_mt;
        damage_to_mt = 0;
        if (life <= 0)
            mt_alive = false;
    }

nta.system:
    urgent chan SHOOT_TO_MT;
    int damage_to_mt; //where enemies put the damage for the MT

    //turrets --> enemies

    broadcast chan SHOOT_TO_ENEMY;
    EnemyRecord shoot_table[MAX_ENEMIES]; //where turrets can look for enemies positions and
                                          //all the information they need to choose which one
                                          //target

    TargetRecord target_record; //where turrets put the ID of the targetted enemy and the related
                                //damage 

circlec(const int id) = compactEnemy(&shoot_table, &target_record, &SHOOT_TO_MT, &damage_to_mt, &SHOOT_TO_ENEMY, id, CIRCLE.speed, CIRCLE.health, CIRCLE.damage, CIRCLE.spawningTime, CIRCLE.kind);
squarec(const int id) = compactEnemy(&shoot_table, &target_record, &SHOOT_TO_MT, &damage_to_mt, &SHOOT_TO_ENEMY, id, SQUARE.speed, SQUARE.health, SQUARE.damage, SQUARE.spawningTime, SQUARE.kind);
basic(const int id, const Cell pos) = turret(&shoot_table, &target_record, &SHOOT_TO_ENEMY,id, pos, BASIC.range, BASIC.speed, BASIC.damage);
cannon(const int id, const Cell pos) = turret(&shoot_table, &target_record, &SHOOT_TO_ENEMY,id, pos, CANNON.range, CANNON.speed, CANNON.damage);
sniper(const int id, const Cell pos) = turret(&shoot_table, &target_record, &SHOOT_TO_ENEMY,id, pos, SNIPER.range, SNIPER.speed, SNIPER.damage);

enc0 = circlec(0);
enc1 = squarec(1);
enc2 = circlec(2);
enc3 = squarec(3);
enc4 = circlec(4);
enc5 = squarec(5);
mt = mainTower(&SHOOT_TO_MT, &damage_to_mt, MT.health);
t0 = basic(0, TURRET0_POS);
t1 = cannon(1, TURRET1_POS);
t2 = cannon(2, TURRET2_POS);
t3 = cannon(3, TURRET3_POS);
t4 = cannon(4, TURRET4_POS);
t5 = sniper(5, TURRET5_POS);
t6 = sniper(6, TURRET6_POS);

system mt,enc0,enc1,enc2, t0, t1, t2, t3, t4, t5, t6;
