nta.declaration:
//type definitions:

typedef struct {
    int[-1,15] x;
    int[-1,15] y;
} Cell;

typedef struct {
    int health;
} MainTowerConfiguration;

typedef struct {
    int speed;
    int health;
    int damage;
    int spawningTime;
} EnemyConfiguration;

typedef struct {
    int range;
    int speed;
    int damage;
} TurretConfiguration;

typedef struct {
    Cell pos;
    int lifeTime;
    bool kind;
    bool available;
} EnemyRecord;

typedef struct {
    int id;
    int damage;
} TargetRecord;

//configuration definitions:

const MainTowerConfiguration MT := {10};

const EnemyConfiguration SQUARE := {3, 20, 4, 3};
const EnemyConfiguration CIRCLE := {1, 10, 2, 2};

const TurretConfiguration BASIC := {2,2,2};
const TurretConfiguration CANNON := {1,7,5};
const TurretConfiguration SNIPER := {4,20,8};

//map definitions

const Cell MAIN_POS = {15,4};

const Cell TURRET0_POS = {5,5};
const Cell TURRET1_POS = {8,2};
const Cell TURRET2_POS = {8,6};
const Cell TURRET3_POS = {14,2};
const Cell TURRET4_POS = {14,6};
const Cell TURRET5_POS = {2,3};
const Cell TURRET6_POS = {11,5};

const Cell INVALID_POS = {-1,-1};
const Cell START_POS = {0,0};

const int MAX_PATH_LENGTH = 25;

//wave parameters

const int MAX_ENEMIES := 3;
int ENEMIES_COUNT = MAX_ENEMIES;

//channels and shared parameters

    //enemies --> MT

    urgent chan STMT;
    int DAMAGE_TO_MT;

    //turrets --> enemies

    EnemyRecord SHOOT_TABLE[MAX_ENEMIES];

    broadcast chan SHOOT_TO_ENEMY;

    TargetRecord TARGET_RECORD;

   // int countEnemies=MAX_ENEMIES;

bool isValid (Cell c)
    {
        if (c == INVALID_POS)
            {
                return (true);
            }
        if (c.y == 7)
            {
                return (c.x>=7 && c.x<=15);
            } 
        if (c.y == 6 || c.y == 5)
            {
                return (c.x == 7 || c.x == 10 || c.x == 15);
            }
        if (c.y == 4)
            {
                return (c.x != 8 && c.x != 9);
            }
        if (c.y == 3 || c.y == 2)
            {
                return (c.x == 0 || c.x == 7 || c.x == 15);
            }
        if (c.y == 1)
            {
                return (c.x == 0 || (c.x >=7 && c.x<=15));
            }
        if (c.y == 0)
            {
                return (c.x == 0);
            }

        return (false);
    }

template.declaration:
int c1 := 0;
int c2 := 0;
Cell pos := INVALID_POS;
int age := 0;
int choice := 0;

void initEnemy (int i)
    {
        pos = START_POS;
        SHOOT_TABLE[id].lifeTime = 0;
        SHOOT_TABLE[id].kind = kind;
        SHOOT_TABLE[id].available = true;
        choice = i;
    }
void takeDamage ()
    {
        if (TARGET_RECORD.id == id)
            {
                health = health - TARGET_RECORD.damage;
                TARGET_RECORD.damage = 0;
            }
    }
void dismiss ()
    {
        SHOOT_TABLE[id].available = false;
        ENEMIES_COUNT--;
    }
void initRecord ()
    {
        SHOOT_TABLE[id].pos = pos;
        SHOOT_TABLE[id].lifeTime = age;
    }
void updateRecord ()
    {
        SHOOT_TABLE[id].pos = pos;
        age++;
        SHOOT_TABLE[id].lifeTime = age;        
    }
void next()
    {
        if (pos.x == 0)
            {
                if (pos.y <=3)
                    pos.y = pos.y+1;
                else
                    if (pos.y == 4)
                        pos.x = 1;

                return;
            }
        if (pos.x >=1 && pos.x <= 6)
            {
                pos.x = pos.x +1;
                return;
            }
        if (pos.x == 7)
            {
                if (pos.y == 4)
                    {
                        if (choice == 0 || choice == 1)
                            pos.y = pos.y + 1;
                        else
                            pos.y = pos.y - 1;
                    }
                else
                    if (pos.y == 5 || pos.y == 6)
                        pos.y = pos.y + 1;
                    else
                        if (pos.y == 3 || pos.y == 2)
                            pos.y = pos.y - 1;
                        else
                            if (pos.y == 7 || pos.y == 1)
                                pos.x = pos.x + 1;
                return;
            }
        if (pos.x >= 8 && pos.x <= 9)
            {
                if (pos.y == 7 || pos.y == 1)
                    pos.x = pos.x + 1;
                return;
            }
        if (pos.x == 10)
            {
                if (pos.y == 7)
                    {
                        if (choice == 0)
                            pos.y = pos.y - 1;
                        else
                            pos.x = pos.x + 1;
                    }
                else
                    if (pos.y == 6 || pos.y == 5)
                        pos.y = pos.y - 1;
                    else
                        if (pos.y == 1 || pos.y == 4)
                            pos.x = pos.x + 1;
                return;
            }
        if (pos.x >= 11 && pos.x <= 14)
            {
                if (pos.y == 7 || pos.y == 1 || pos.y == 4)
                    pos.x = pos.x + 1;
                return;
            }
        if (pos.x == 15)
            {
                if (pos.y < 4 && pos.y >= 1)
                    pos.y = pos.y + 1;
                else
                    if (pos.y > 4 && pos.y <= 7)
                        pos.y = pos.y - 1;
                return;
            }
    }

int c1=0;

int max (int v1, int v2)
    {
        if (v1 >= v2)
            return (v1);
        else
            return (v2);
    }
int dist(const Cell c1, const Cell c2)
    {
        return (max(abs(c1.x - c2.x), abs(c1.y - c2.y)));
    }
void target (int id)
    {
        TARGET_RECORD.id = id;
        TARGET_RECORD.damage = damage;
        c1=speed;
    }
void aim()
    {
        int k, i, app[MAX_ENEMIES], appLen = 0, maxLife, lifeLen = 0;

        for (k = 1 ; k<=range ; k++)
            {
                for (i = 0 ; i < MAX_ENEMIES ; i++)
                    {
                        if (SHOOT_TABLE[i].available && dist(SHOOT_TABLE[i].pos, pos) == k)
                            {
                                app[appLen] = i;
                                appLen++;
                            }
                    }
                if (appLen > 0)
                    {
                        maxLife = SHOOT_TABLE[app[0]].lifeTime;
                        lifeLen = 1;
                        for (i = 1 ; i < appLen ; i++)
                            {
                                if (SHOOT_TABLE[app[i]].lifeTime > maxLife)
                                    {
                                        maxLife = SHOOT_TABLE[app[i]].lifeTime;
                                        app[0] = app[i];
                                        lifeLen=1;
                                    }
                                else
                                    {
                                        if (SHOOT_TABLE[app[i]].lifeTime == maxLife)
                                            {
                                                app[lifeLen] = app[i];
                                                lifeLen++;
                                            }
                                    }
                            }
                        lifeLen--;
                        for (i = 0 ; i < lifeLen ; i++)
                            {
                                if (SHOOT_TABLE[app[i]].kind)
                                    {
                                        target(app[i]);
                                        return;
                                    }
                            }
                        target(app[i]);
                        return;
                    }
            }                
    }
bool shootable()
    {
        int i;

        for (i = 0 ; i<MAX_ENEMIES ; i++)
            {
                if (SHOOT_TABLE[i].available && dist(SHOOT_TABLE[i].pos, pos)<=range)
                    {
                         return (true);
                    }
            }

        return (false);
    }

void decDamage()
    {
        life = life - DAMAGE_TO_MT;
        DAMAGE_TO_MT = 0;
    }

nta.system:
circlec(const int id) = compactEnemy(id, CIRCLE.speed, CIRCLE.health, CIRCLE.damage, CIRCLE.spawningTime, false);
squarec(const int id) = compactEnemy(id, SQUARE.speed, SQUARE.health, SQUARE.damage, SQUARE.spawningTime, true);
basic(const int id, const Cell pos) = turret(id, pos, BASIC.range, BASIC.speed, BASIC.damage);
cannon(const int id, const Cell pos) = turret(id, pos, CANNON.range, CANNON.speed, CANNON.damage);
sniper(const int id, const Cell pos) = turret(id, pos, SNIPER.range, SNIPER.speed, SNIPER.damage);

enc0 = circlec(0);
enc1 = squarec(1);
enc2 = circlec(2);
enc3 = squarec(3);
mt = mainTower(MT.health);
t0 = basic(0, TURRET0_POS);
t1 = cannon(1, TURRET1_POS);
t2 = cannon(2, TURRET2_POS);
t3 = cannon(3, TURRET3_POS);
t4 = cannon(4, TURRET4_POS);
t5 = sniper(5, TURRET5_POS);
t6 = sniper(6, TURRET6_POS);

system mt, enc0, enc1, t0;//, enc2;//, enc1;//, enc1, enc2;//, t0, t5;//, en1, t0, t5;
