nta.declaration:
//type definitions:

typedef struct {
    int x;
    int y;
} Cell;

typedef struct {
    int health;
} MainTowerConfiguration;

typedef struct {
    int speed;
    int health;
    int damage;
    int spawningTime;
} EnemyConfiguration;

typedef struct {
    int range;
    int speed;
    int damage;
} TurretConfiguration;

typedef struct {
    Cell pos;
    int lifeTime;
    bool kind;
    int DAMAGE_TO_ENEMY;
    bool available;
} EnemyRecord;

//configuration definitions:

const MainTowerConfiguration MT := {300};

const EnemyConfiguration SQUARE := {3, 20, 4, 3};
const EnemyConfiguration CIRCLE := {1, 10, 2, 2};

const TurretConfiguration BASIC := {2,2,2};
const TurretConfiguration CANNON := {1,7,5};
const TurretConfiguration SNIPER := {4,20,8};

//map definitions

const Cell MAIN_POS = {15,4};

const Cell TURRET0_POS = {2,3};
const Cell TURRET1_POS = {5,5};
const Cell TURRET2_POS = {8,2};
const Cell TURRET3_POS = {8,6};
const Cell TURRET4_POS = {11,5};
const Cell TURRET5_POS = {14,2};
const Cell TURRET6_POS = {14,6};

const Cell ENEMIES_PATH1[12] = {{0,0},{0,1},{0,2},{0,3},{0,4},{1,4},{2,4},{3,4},{4,4},{5,4},{6,4},{7,4}};
const Cell ENEMIES_PATH2[6] = {{7,5},{7,6},{7,7},{8,7},{9,7},{10,7}};
const Cell ENEMIES_PATH3[8] = {{11,7},{12,7},{13,7},{14,7},{15,7},{15,6},{15,5},{15,4}};
const Cell ENEMIES_PATH4[8] = {{10,6},{10,5},{10,4},{11,4},{12,4},{13,4},{14,4},{15,4}};
const Cell ENEMIES_PATH5[14] = {{7,3},{7,2},{7,1},{8,1},{9,1},{10,1},{11,1},{12,1},{13,1},{14,1},{15,1},{15,2},{15,3},{15,4}};

const Cell INVALID_POS = {-1,-1};
const Cell START_POS = {0,0};

//wave parameters

const int MAX_ENEMIES := 4;

//channels and shared parameters

    //enemies --> MT

    urgent chan STMT;
    int DAMAGE_TO_MT;

    //turrets --> enemies

    EnemyRecord SHOOT_TABLE[MAX_ENEMIES];
   // int countEnemies=MAX_ENEMIES;

template.declaration:
int c1=0;

int max (int v1, int v2)
    {
        if (v1 >= v2)
            return (v1);
        else
            return (v2);
    }
int dist(Cell c1, Cell c2)
    {
        return (max(abs(c1.x - c2.x), abs(c1.y - c2.y)));
    }
void shoot()
    {
        int k, i, app[MAX_ENEMIES], appLen = 0, maxLife, lifeLen = 0;

        for (k = 1 ; k<=range ; k++)
            {
                for (i = 0 ; i < MAX_ENEMIES ; i++)
                    {
                        if (SHOOT_TABLE[i].available && dist(SHOOT_TABLE[i].pos, pos) == k)
                            {
                                app[appLen] = i;
                                appLen++;
                            }
                    }
                if (appLen > 0)
                    {
                        maxLife = SHOOT_TABLE[app[0]].lifeTime;
                        lifeLen = 1;
                        for (i = 1 ; i < appLen ; i++)
                            {
                                if (SHOOT_TABLE[app[i]].lifeTime > maxLife)
                                    {
                                        maxLife = SHOOT_TABLE[app[i]].lifeTime;
                                        app[0] = app[i];
                                        lifeLen=1;
                                    }
                                else
                                    {
                                        if (SHOOT_TABLE[app[i]].lifeTime == maxLife)
                                            {
                                                app[lifeLen] = app[i];
                                                lifeLen++;
                                            }
                                    }
                            }
                        lifeLen--;
                        for (i = 0 ; i < lifeLen ; i++)
                            {
                                if (SHOOT_TABLE[app[i]].kind)
                                    {
                                        SHOOT_TABLE[app[i]].DAMAGE_TO_ENEMY = damage;
                                        c1=0;
                                        return;
                                    }
                            }
                        SHOOT_TABLE[app[i]].DAMAGE_TO_ENEMY = damage;
                        c1=0;
                        return;
                    }
            }
    }

int c1 := 0;
int c2 := 0;
Cell pos := INVALID_POS;
int current_index := 1;
int current_path := 1;

/*
    problemi rimasti:
    -> STMT non sincronizzato con ENEMY_SYNC;
    -> alcune volte si cambia la pos. senza aspettare il delay (probabilmente si sovrappongono le guard)
*/

void initEnemy ()
    {
        pos = START_POS;
        SHOOT_TABLE[id].lifeTime = 0;
        SHOOT_TABLE[id].kind = kind;
        SHOOT_TABLE[id].DAMAGE_TO_ENEMY = 0;
        SHOOT_TABLE[id].available = true;
    }
void dismiss ()
    {
        SHOOT_TABLE[id].available = false;
    }
void updateRecord ()
    {
        SHOOT_TABLE[id].pos = pos;
        SHOOT_TABLE[id].lifeTime++;
        health = health - SHOOT_TABLE[id].DAMAGE_TO_ENEMY;
        SHOOT_TABLE[id].DAMAGE_TO_ENEMY = 0;
        if (health<=0)
            {
                SHOOT_TABLE[id].available = false;
            }

    }
void changePath(int i)
    {
        current_index = 0;
        current_path = i;
    }
void next()
    {
        if (current_path == 1)
            {
                pos = ENEMIES_PATH1[current_index];
            }
        if(current_path == 2)
            {
                pos = ENEMIES_PATH2[current_index];
            }
        if (current_path == 3)
            {
                pos = ENEMIES_PATH3[current_index];
            }
        if (current_path == 4)
            {
                pos = ENEMIES_PATH4[current_index];
            }
        if (current_path == 5)
            {
                pos = ENEMIES_PATH5[current_index];
            }

        current_index++;
        c1=0;
    }

nta.system:
circle(const int id) = enemy(id, CIRCLE.speed, CIRCLE.health, CIRCLE.damage, CIRCLE.spawningTime, false);
square(const int id) = enemy(id, SQUARE.speed, SQUARE.health, SQUARE.damage, SQUARE.spawningTime, true);
basic(const int id, const Cell pos) = turret(id, pos, BASIC.range, BASIC.speed, BASIC.damage);
cannon(const int id, const Cell pos) = turret(id, pos, CANNON.range, CANNON.speed, CANNON.damage);
sniper(const int id, const Cell pos) = turret(id, pos, SNIPER.range, SNIPER.speed, SNIPER.damage);

en0 = circle(0);
en1 = square(1);
//en2 = circle(0);
//en3 = square(1);
mt = mainTower(MT.health);
t0 = basic(0, TURRET0_POS);



system en0, en1, /*en2, en3*/mt, t0;
