nta.declaration:
//type definitions:

typedef struct {
    int[-1,15] x;
    int[-1,15] y;
} Cell;

typedef struct {
    int health;
} MainTowerConfiguration;

typedef struct {
    int speed;
    int health;
    int damage;
    int spawningTime;
} EnemyConfiguration;

typedef struct {
    int range;
    int speed;
    int damage;
} TurretConfiguration;

typedef struct {
    Cell pos;
    int lifeTime;
    bool kind;
    bool available;
} EnemyRecord;

typedef struct {
    int id;
    int damage;
} TargetRecord;

//configuration definitions:

const MainTowerConfiguration MT := {10};

const EnemyConfiguration SQUARE := {3, 20, 4, 3};
const EnemyConfiguration CIRCLE := {1, 10, 2, 2};

const TurretConfiguration BASIC := {2,2,2};
const TurretConfiguration CANNON := {1,7,5};
const TurretConfiguration SNIPER := {4,20,1};//QUI IL DAMAGE DEVE ESSERE "8". L'HO CAMBIATO PER TESTARE

//map definitions

const Cell MAIN_POS = {15,4};

const Cell TURRET0_POS = {5,5};
const Cell TURRET1_POS = {8,2};
const Cell TURRET2_POS = {8,6};
const Cell TURRET3_POS = {14,2};
const Cell TURRET4_POS = {14,6};
const Cell TURRET5_POS = {2,3};
const Cell TURRET6_POS = {11,5};

const Cell ENEMIES_PATH1[12] = {{ 0, 0},{ 0, 1},{ 0, 2},{ 0, 3},{ 0, 4},{ 1, 4},{ 2, 4},{ 3, 4},{ 4, 4},{ 5, 4},{ 6, 4},{ 7, 4}};
const Cell ENEMIES_PATH2[6]  = {{ 7, 5},{ 7, 6},{ 7, 7},{ 8, 7},{ 9, 7},{10, 7}};
const Cell ENEMIES_PATH3[8]  = {{11, 7},{12, 7},{13, 7},{14, 7},{15, 7},{15, 6},{15, 5},{15, 4}};
const Cell ENEMIES_PATH4[8]  = {{10, 6},{10, 5},{10, 4},{11, 4},{12, 4},{13, 4},{14, 4},{15, 4}};
const Cell ENEMIES_PATH5[14] = {{ 7, 3},{ 7, 2},{ 7, 1},{ 8, 1},{ 9, 1},{10, 1},{11, 1},{12, 1},{13, 1},{14, 1},{15, 1},{15, 2},{15, 3},{15, 4}};

const int PATH_LENGHTS[5] = {12, 6, 8, 8, 14};

const Cell INVALID_POS = {-1,-1};
const Cell START_POS = {0,0};

const int MAX_PATH_LENGTH = 26;

//wave parameters

const int MAX_ENEMIES := 2;
int ENEMIES_COUNT = MAX_ENEMIES;

//channels and shared parameters

    //enemies --> MT

    urgent chan STMT;
    int DAMAGE_TO_MT;

    //turrets --> enemies

    EnemyRecord SHOOT_TABLE[MAX_ENEMIES];

    broadcast chan SHOOT_TO_ENEMY;

    TargetRecord TARGET_RECORD;

   // int countEnemies=MAX_ENEMIES;

template.declaration:
int c1=0;

int max (int v1, int v2)
    {
        if (v1 >= v2)
            return (v1);
        else
            return (v2);
    }
int dist(const Cell c1, const Cell c2)
    {
        return (max(abs(c1.x - c2.x), abs(c1.y - c2.y)));
    }
void fire(int id)
    {
        TARGET_RECORD.id = id;
        TARGET_RECORD.damage = damage;
    }
void shoot()
    {
        int k, i, app[MAX_ENEMIES], appLen = 0, maxLife, lifeLen = 0;

        for (k = 1 ; k<=range ; k++)
            {
                for (i = 0 ; i < MAX_ENEMIES ; i++)
                    {
                        if (SHOOT_TABLE[i].available && dist(SHOOT_TABLE[i].pos, pos) == k)
                            {
                                app[appLen] = i;
                                appLen++;
                            }
                    }
                if (appLen > 0)
                    {
                        maxLife = SHOOT_TABLE[app[0]].lifeTime;
                        lifeLen = 1;
                        for (i = 1 ; i < appLen ; i++)
                            {
                                if (SHOOT_TABLE[app[i]].lifeTime > maxLife)
                                    {
                                        maxLife = SHOOT_TABLE[app[i]].lifeTime;
                                        app[0] = app[i];
                                        lifeLen=1;
                                    }
                                else
                                    {
                                        if (SHOOT_TABLE[app[i]].lifeTime == maxLife)
                                            {
                                                app[lifeLen] = app[i];
                                                lifeLen++;
                                            }
                                    }
                            }
                        lifeLen--;
                        for (i = 0 ; i < lifeLen ; i++)
                            {
                                if (SHOOT_TABLE[app[i]].kind)
                                    {
                                        fire(app[i]);
                                        c1=speed;
                                        return;
                                    }
                            }
                        fire(app[i]);
                        c1=speed;
                        return;
                    }
            }
    }

int c1 := 0;
int c2 := 0;
Cell pos := INVALID_POS;
int current_index := 1;
int current_path := 1;
int age := 0;

void initEnemy ()
    {
        pos = START_POS;
        SHOOT_TABLE[id].lifeTime = 0;
        SHOOT_TABLE[id].kind = kind;
        SHOOT_TABLE[id].available = true;
    }
void takeDamage ()
    {
        if (TARGET_RECORD.id == id)
            {
                health = health - TARGET_RECORD.damage;
                TARGET_RECORD.damage = 0;
            }
    }
void dismiss ()
    {
        SHOOT_TABLE[id].available = false;
        ENEMIES_COUNT--;
    }
void initRecord ()
    {
        SHOOT_TABLE[id].pos = pos;
        SHOOT_TABLE[id].lifeTime = age;
    }
void updateRecord ()
    {
        SHOOT_TABLE[id].pos = pos;
        age++;
        SHOOT_TABLE[id].lifeTime = age;
        if (health<=0)
            {
                SHOOT_TABLE[id].available = false;
            }

    }
void changePath(const int i)
    {
        current_index = 0;
        current_path = i;
    }
void next()
    {
        if (current_path == 1)
            {
                pos = ENEMIES_PATH1[current_index];
            }
        if(current_path == 2)
            {
                pos = ENEMIES_PATH2[current_index];
            }
        if (current_path == 3)
            {
                pos = ENEMIES_PATH3[current_index];
            }
        if (current_path == 4)
            {
                pos = ENEMIES_PATH4[current_index];
            }
        if (current_path == 5)
            {
                pos = ENEMIES_PATH5[current_index];
            }

        current_index++;
        c1=0;
    }

void decDamage()
    {
        life = life - DAMAGE_TO_MT;
        DAMAGE_TO_MT = 0;
    }

nta.system:
circle(const int id) = enemy(id, CIRCLE.speed, CIRCLE.health, CIRCLE.damage, CIRCLE.spawningTime, false);
square(const int id) = enemy(id, SQUARE.speed, SQUARE.health, SQUARE.damage, SQUARE.spawningTime, true);
basic(const int id, const Cell pos) = turret(id, pos, BASIC.range, BASIC.speed, BASIC.damage);
cannon(const int id, const Cell pos) = turret(id, pos, CANNON.range, CANNON.speed, CANNON.damage);
sniper(const int id, const Cell pos) = turret(id, pos, SNIPER.range, SNIPER.speed, SNIPER.damage);

en0 = circle(0);
en1 = square(1);
en2 = circle(2);
en3 = square(3);
mt = mainTower(MT.health);
t0 = basic(0, TURRET0_POS);
t1 = cannon(1, TURRET1_POS);
t2 = cannon(2, TURRET2_POS);
t3 = cannon(3, TURRET3_POS);
t4 = cannon(4, TURRET4_POS);
t5 = sniper(5, TURRET5_POS);
t6 = sniper(6, TURRET6_POS);

